import React from "react";
import Header from "../../components/header/Header.js";
import Footer from "../../components/footer/Footer.js";
import "./login.css";
import ReactDOM from 'react-dom';
import login_block_img from "../../static/login_block_img.png";
import logo_vk from "../../static/vk_logo.png";
import logo_gosuslugi from "../../static/logo_gosuslugi.png";
import logo_google from "../../static/logo_google.png";
import logo_yandex from "../../static/logo_yandex.png";
import checkmark from "../../static/checkmark.png";
import { Link } from "react-router-dom";
import axios from 'axios';
import './pretty-checkbox.min.css';

class LoginForm extends React.Component {
    constructor(props) {
        super(props);
        this.handleClick = this.handleClick.bind(this);
        this.onBoxChange = this.onBoxChange.bind(this);
        this.state = {
            name: "",
            last_name: "",
            login: "",
            nickname: "",
            email: "",
            phone: "",
            password1: "",
            password2: "",
            req1: false,
            req2: false,
            req3: false,
            reg_status: 0,
            box: false,
        }
    }

    handleClick(e) {
        
    }

    onBoxChange(e) {
        if (e.target.checked) {
            this.setState({box: true});
        } else {
            this.setState({box: false});
        }
    }

    checkPassword(pass) {
        let status = {len: false, bigIn: false, numIn: false};
        let r1 = new RegExp("[A-ZА-Я]");
        let r2 = new RegExp("[0-9]");
        if (pass.length >= 8){
            status["len"] = true;
        }
        if (r1.test(pass)) {
            status["bigIn"] = true;
        }
        if (r2.test(pass)) {
            status["numIn"] = true;
        }
        this.setState({req1: status.len, req2: status.bigIn, req3: status.numIn})
        return status;
    }

    onTextChange = event => {
        let tmp
        switch (event.target.id ){
            case "input_name": 
                this.setState({name: event.target.value});
                tmp = this.state;
                tmp.name = event.target.value;
                this.props.changeFunc(tmp);
                break;
            case "input_last_name": 
                this.setState({last_name: event.target.value});
                tmp = this.state;
                tmp.last_name = event.target.value;
                this.props.changeFunc(tmp);
                break;
            case "input_login": 
                this.setState({login: event.target.value});
                tmp = this.state;
                tmp.login = event.target.value;
                this.props.changeFunc(tmp);
                break;
            case "input_nickname": 
                this.setState({nickname: event.target.value});
                tmp = this.state;
                tmp.nickname = event.target.value;
                this.props.changeFunc(tmp);
                break;
            case "input_mail": 
                this.setState({email: event.target.value});
                tmp = this.state;
                tmp.email = event.target.value;
                this.props.changeFunc(tmp);
                break;
            case "input_phone": 
                this.setState({phone: event.target.value});
                tmp = this.state;
                tmp.phone = event.target.value;
                this.props.changeFunc(tmp);
                break;
            case "input_pass1": 
                this.setState({password1: event.target.value});
                tmp = this.state;
                tmp.password1 = event.target.value;
                this.props.changeFunc(tmp);
                let status = this.checkPassword(event.target.value);
                if ((!status.len) || (!status.bigIn) || (!status.numIn)) {
                    event.target.style.backgroundColor = "#fc8e86";
                } else {
                    event.target.style.backgroundColor = "#6CECC7";
                }
                let pass2 = ReactDOM.findDOMNode(document.getElementById("input_pass2"));
                if (pass2.value != event.target.value) {
                    pass2.style.backgroundColor = "#fc8e86";
                } else {
                    pass2.style.backgroundColor = "#6CECC7";
                }
                break;
            case "input_pass2": 
                this.setState({password2: event.target.value});
                if (this.state.password1 != event.target.value) {
                    event.target.style.backgroundColor = "#fc8e86";
                } else {
                    event.target.style.backgroundColor = "#6CECC7";
                }
                break;
        }
    }

    render() {
        return (
            <div className="log_block" style={this.props.style}>
                <div className="log_title">
                    <h style={{marginTop: "40px"}}>Авторизация</h>
                    <img src={login_block_img} width="400px" style={{marginTop: "20px"}}></img>
                </div>
                <div className="log_main">
                    <div className="inputs">
                        <input type="text" placeholder="Логин / почта" className="log_login_input"></input>
                        <input type="text" placeholder="Пароль" className="log_password_input"></input>
                    </div>
                    <div className="log_proceed">
                        <div style={{display: "block", marginLeft: "21px"}}>
                            <div style={{display: "flex", alignItems: "center"}}>
                                <input type="checkbox" className="login_checkbox"></input>
                                <label style={{marginLeft: "5px", fontSize: "15px"}}>Запомнить пароль</label>
                            </div>
                            <button className="login_btn">
                                Войти
                            </button>
                        </div>
                        <hr style={{direction: "vertical", border: " 1px solid black", marginTop: "0px"}}/>
                        <div style={{display: "block", marginLeft: "3px", width: "50%"}}>
                                <div style={{cursor: "pointer", userSelect: "none"}}>
                                    <h style={{color: "white", fontSize: "15px"}}>
                                        Войти с помощью
                                    </h>
                                </div>
                            <div style={{display: "flex", marginTop: "5px"}}>
                                <img style={{userSelect: "none"}} src={logo_vk} width="30px" height="30px"></img>
                                <img style={{marginLeft: "10px", userSelect: "none"}} src={logo_google} height="30px"></img>
                                <img style={{marginLeft: "10px", userSelect: "none"}} src={logo_gosuslugi} height="30px"></img>
                                <img style={{marginLeft: "10px", userSelect: "none"}} src={logo_yandex} height="30px"></img>
                            </div>
                            <div style={{marginTop: "5px", cursor: "pointer", userSelect: "none"}}>
                                <h style={{color: "white", fontSize: "15px"}}>
                                    Еще не зарегистрированы?
                                </h>
                            </div>
                        </div>
                    </div>
                </div>
            </div>
        )
    }
}

class Login extends React.Component {
    constructor() {
        super();
        this.data1 = React.createRef();
        this.data2 = React.createRef();
        this.onInputChange = this.onInputChange.bind(this);
        this.state = {
            name: "",
            last_name: "",
            login: "",
            nickname: "",
            email: "",
            phone: "",
            password: "",
            reg_display: "block",
            conf_display: "none",
            conf_answer: ""
        }
    }

    onInputChange(e) {
        this.setState({
            name: e.name,
            last_name: e.last_name,
            login: e.login,
            nickname: e.nickname,
            email: e.email,
            password: e.password1,
            phone: e.phone
        });

    }

    render() {
        return (
            <div className="log_wrap">
                <LoginForm changeFunc={this.onInputChange} style={{display: this.state.reg_display}}/>
            </div>
        )
    }
}
export default Login;
