import React from 'react';
import './TextInput.css'

class TextInput extends React.Component {
    state = {
        text: "",
    };
    onTextChange = event => {
        this.setState({
            text: event.target.value
        });
        console.log(this.state.text);
    }
    render() {
        return (
            <input onChange={this.onTextChange} type='text' id={this.props.id} placeholder={this.props.ph} style={{marginTop: this.props.top}}></input>
        )
    }
};

export default TextInput;
