import React from "react";
import "./users_profile.css";
import ReactDOM from 'react-dom';
import { Link, useNavigation } from "react-router-dom";
import axios from 'axios';
import Cookies from 'universal-cookie';
import Header from '../../components/header/Header.js';

const cookies = new Cookies();
const navigate = useNavigation();


class UsersProfile extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            uid: ""
        }
        var jwt = cookies.get("jwt");
        const config = {
            headers: {
                'Content-Type': 'application/json',
                'authorization': `Bearer ${jwt}`
            }
        }
        var resp;
        axios.get( "http://localhost:3010/api/checkjwt", config ).then(response => resp = response).catch(function (error) {
            if (error.response) {
                alert("Ошибка " + String(error.response.status));
            }
        })
        if (resp && resp.data) {
            if (resp.data["status"] == 1) {
                navigate("/login", { replace: true });
            } else {
                this.setState({uid: resp.data.data});
            }
        }
        this.handleClick = this.handleClick.bind(this);
        this.onTextChange = this.onTextChange.bind(this);
    }

    render() {
        return (
            <div className="foreign_profie_wrap">
                <Header isMain={false} />
                <div className="nav_icon">
                    <span className="bar_big"></span>
                    <span className="bar_small"></span>
                    <span className="bar_small"></span>
                    <span className="bar_big"></span>
                </div>
            </div>
        )
    }
}

class UsersProfil1e extends React.Component {
    constructor() {
        super();
        this.data1 = React.createRef();
        this.data2 = React.createRef();
        this.onInputChange = this.onInputChange.bind(this);
        this.state = {
            name: "",
            last_name: "",
            login: "",
            nickname: "",
            email: "",
            phone: "",
            password: "",
            reg_display: "block",
            conf_display: "none",
            conf_answer: ""
        }
    }

    onInputChange(e) {
        this.setState({
            name: e.name,
            last_name: e.last_name,
            login: e.login,
            nickname: e.nickname,
            email: e.email,
            password: e.password1,
            phone: e.phone
        });

    }

    render() {
        return (
            <div className="log_wrap">
                <LoginForm changeFunc={this.onInputChange} style={{display: this.state.reg_display}}/>
            </div>
        )
    }
}
export default UsersProfile;
