import React from "react";
import "./users_profile.css";
import ReactDOM from 'react-dom';
import { Link, useNavigate } from "react-router-dom";
import axios from 'axios';
import Cookies from 'universal-cookie';
import Header from '../../components/header/Header.js';

const cookies = new Cookies();


class UsersProfileClass extends React.Component {
    constructor(props) {
        super(props);
        const navigate = this.props.navigate;
        console.log(cookies.getAll());
        this.state = {
            uid: ""
        }
        var jwt = cookies.get("jwt");
        const config = {
            headers: {
                'Content-Type': 'application/json',
                'authorization': `Bearer ${jwt}`
            }
        }
        var resp;
        axios.get( "http://localhost:3010/api/checkjwt", config ).then(function(response) {
            if ((Object.keys(response).length != 0) && (Object.keys(response.data).length != 0)) {
                if (response.data["status"] == 1) {
                    navigate("/login", { replace: true });
                } else {
                    this.setState({uid: response.data.data});
                }
            }
        }).catch(function (error) {
            if (error.response) {
                alert("Ошибка " + String(error.response.status));
            }
        });
        
        
        // this.handleClick = this.handleClick.bind(this);
        // this.onTextChange = this.onTextChange.bind(this);
    }

    onNavClick(e) {
        if (e.target.className == "nav_icon") {
            e.target.className = "nav_icon_active";
        } else {
            e.target.className = "nav_icon"
        }
    }

    render() {
        return (
            <div className="foreign_profie_wrap">
                <Header isMain={false} />
                <div className="nav_icon" onClick={onNavClick}>
                    <span className="bar_big"></span>
                    <span className="bar_small1"></span>
                    <span className="bar_small2"></span>
                    <span className="bar_big"></span>
                </div>
                <ul className="menu">
                    <li className="menu_cross">

                    </li>
                    <li className="menu_item">
                        Главная
                    </li>
                    <li className="menu_item">
                        Сообщения
                    </li>
                    <li className="menu_item">
                        Настройки
                    </li>
                </ul>
            </div>
        )
    }
}

export default function UsersProfile() {
    const navigate = useNavigate();
  
    return <UsersProfileClass navigate={navigate} />;
  }
