import React, { useEffect, useState } from "react";
import "./Feed.css";
import ReactDOM from 'react-dom';
import { Link, useNavigate, useParams } from "react-router-dom";
import axios from 'axios';
import Cookies from 'universal-cookie';
import Header from '../../components/header/Header.js';
import subs from "../../static/my_profile_vct1.svg";
import back1 from "../../static/my_profile_img1.png";
import back2 from "../../static/my_profile_img2.png";
import back3 from "../../static/my_profile_img3.png";
import cross_img from "../../static/black_cross.png";
import places_visited from "../../static/my_profile_vct2.svg";
import top_place from "../../static/my_profile_vct3.svg";
import Footer from "../../components/footer/Footer.js";
import { Map, YMaps, Placemark } from '@pbe/react-yandex-maps';

const cookies = new Cookies();

const PostItem = (post) => {
    return (
        <div className="post_item_wrapper">
            <h>post</h>
        </div>
    )
}

export default function Feed() {
    const [currentPostStart,setCurrentPostStart]=useState(0);
    const [isMyFetching,setIsFetchingDown]=useState(false);
    const [isMyFetchingUp,setIsMyFetchingUp]=useState(false);
    let posts = [];

    useEffect(() => {
        if(isMyFetching)
        {
            setCurrentPostStart(prev=>{
                return prev < 93 ? prev + 1 : prev
            })
            setIsFetchingDown(false)  
        }
    }, [isMyFetching])
    useEffect(() => {
    if(isMyFetchingUp)
    {
        setCurrentPostStart(prev => {
            return prev > 0 ? prev - 1 : prev
        })
        setIsMyFetchingUp(false)  
    }
    }, [isMyFetchingUp])
    useEffect(() => {
      document.addEventListener('scroll', scrollHandler)
      return () => {
        document.removeEventListener('scroll', scrollHandler)
      }
    }, [])
    const scrollHandler = (e) => {
        console.log(e);
        if(e.target.documentElement.scrollTop < 50)
        {
            setIsMyFetchingUp(true)
        }
        if(e.target.documentElement.scrollHeight-e.target.documentElement.scrollTop-window.innerHeight < 50)
        {
            setIsFetchingDown(true)
            window.scrollTo(0, (e.target.documentElement.scrollHeight + e.target.documentElement.scrollTop));
        }
    }

    return (
        <div>
            <div className='posts_list'>
                {posts?.map(post => <PostItem key={post.id} post={post}/>)}
            </div>
        </div>
    );
}
