import "./Footer.css";
import TextInput from "../common/TextInput/TextInput.js"
import React from "react";

class Footer extends React.Component {
    constructor(props) {
        super(props);
        this.email = React.createRef();
        this.handleClick = this.handleClick.bind(this);
    }
    handleClick(e) {
        const current_email = this.email.current;
        alert(current_email.state.text);
        
    }
    render() {
        return (
            <footer style={this.props.style}>
                <div className="upper">
                    <div className="div_news">
                        <u>
                            Подпишись на новости:
                        </u>
                        <TextInput ref={this.email} id="footer_news_email" ph="e-mail" top="10px"></TextInput>
                        <div style={{display: "inline-flex", width: "300px"}}>
                            <button className="footer_news_btn" onClick={this.handleClick}>
                                Подписаться
                            </button>

                        </div>
                    </div>
                </div>
            </footer>
    )
    }
}

export default Footer;
