import "./Footer.css";
import TextInput from "../common/TextInput/TextInput.js"
import React from "react";

function Footer(props) {
    this.email = React.createRef();
    function handleClick() {
        const current_email = Footer.email.current;
        alert(current_email);
    }
    return (
        <footer style={{background: props.back}}>
            <div className="upper">
                <div>
                    <u>
                        Подпишись на новости:
                    </u>
                    <TextInput ref={this.email} id="footer_news_email" ph="e-mail" top="10px"></TextInput>
                    <div style={{display: "inline-flex", width: "300px"}}>
                        <button className="footer_news_btn" onClick={handleClick}>
                            Подписаться
                        </button>

                    </div>
                </div>
            </div>
        </footer>
    )
}

export default Footer;
