import React from "react";
import "./users_profile.css";
import ReactDOM from 'react-dom';
import { Link } from "react-router-dom";
import axios from 'axios';
import Cookies from 'universal-cookie';
import Header from '../../components/header/Header.js';

const cookies = new Cookies();


class UsersProfile extends React.Component {
    constructor(props) {
        super(props);
        const { navigate } = this.props.navigate;
        this.state = {
            uid: ""
        }
        var jwt = cookies.get("jwt");
        const config = {
            headers: {
                'Content-Type': 'application/json',
                'authorization': `Bearer ${jwt}`
            }
        }
        var resp;
        axios.get( "http://localhost:3010/api/checkjwt", config ).then(response => resp = response).then(console.log(resp)).catch(function (error) {
            if (error.response) {
                alert("Ошибка " + String(error.response.status));
            }
        }).then(console.log(resp))
        
        if (resp && resp.data) {
            if (resp.data["status"] == 1) {
                navigate("/login", { replace: true });
            } else {
                this.setState({uid: resp.data.data});
            }
        }
        // this.handleClick = this.handleClick.bind(this);
        // this.onTextChange = this.onTextChange.bind(this);
    }

    render() {
        return (
            <div className="foreign_profie_wrap">
                <Header isMain={false} />
                <div className="nav_icon">
                    <span className="bar_big"></span>
                    <span className="bar_small"></span>
                    <span className="bar_small"></span>
                    <span className="bar_big"></span>
                </div>
            </div>
        )
    }
}

export default UsersProfile;
